/* eslint-disable react/prop-types */

import styled from 'styled-components';

const Product = ({ photo, title, author }) => {
  return (
    <ProductStyles>
      <img src={photo.image.publicUrlTransformed} alt={photo.altText} />
      <h4>{title}</h4>
      <p
        style={{
          color: 'var(--cream)',
          margin: '7px',
        }}
      >
        {author}
      </p>
      <hr
        style={{
          backgroundColor: 'var(--cream)',
          opacity: '0.4',
          width: '30px',
          margin: '0 auto',
          height: '1px',
          border: 'none',
        }}
      />
    </ProductStyles>
  );
};

export default Product;
