const Product = ({ photo, title, author }) => {
  const ProductStyles = styled.div`
  display: flex;
  flex-direction: column;

  img {
    height: 180px;
    width: 110px;
    object-fit: cover;
    margin: 10px auto;
  }

  h4 {
    text-transform: uppercase;
    font-family: var(--titleFont);
  }
`;
  return (
    <ProductStyles>
    <img
              src={elm.photo.image.publicUrlTransformed}
              alt={elm.photo.altText}
            />
            <h4>{elm.title}</h4>
            <p
              style={{
                color: 'var(--cream)',
                margin: '7px',
              }}
            >
              {elm.author}
            </p>
            <hr
              style={{
                backgroundColor: 'var(--cream)',
                opacity: '0.4',
                width: '30px',
                margin: '0 auto',
                height: '1px',
                border: 'none',
              }}
      />
      </ProductStyles>
  )
}
