import styled from 'styled-components';

const PriceTag = styled.span`
  background: rgb(var(--cream));
  color: rgb(var(--turquoise));
  padding: 5px 15px;
  line-height: 1;
  font-size: 1.5rem;
  display: inline-block;
  position: absolute;
  top: 160px;
  right: -10px;
  border: 1px solid rgb(var(--mocha));

  &::before {
    content: ' ';
    display: block;
    width: 30px;
    height: 100%;
    background: rgb(var(--cream));
    position: absolute;
    top: -10px;
    right: -20px;
    border-right: 17px solid white;
    border-top: 17px solid rgb(var(--cream));
    border-bottom: 17px solid rgb(var(--cream));
  }

  &::after {
    content: ' ';
    display: block;
    width: 10px;
    height: 10px;
    background: rgb(var(--mocha));
    position: absolute;
    top: -15px;
  }
`;

export default PriceTag;
