import styled from 'styled-components';

const HeroStyles = styled.div`
  min-height: 500px;
  font-family: var(--textFont);
  font-size: 3em;
  width: 100vw;
  margin: 0;
  padding: 0;
  position: relative;

  & :before {
    content: ' ';
    display: block;
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    z-index: 1;
    opacity: 0.6;
    background: url('https://res.cloudinary.com/utime/image/upload/v1614854500/UTime/kaboompics_Books_-_Coffee_-_Cinnamon_Roll_dwahl1.jpg')
      center/cover no-repeat;
   }
`;

const Hero = () => (
  <HeroStyles>
        Make a coffee.
        <br />
        Light a candle.
        <br />
        Open a book.
        <br />
        This is your moment.
  </HeroStyles>
);

export default Hero;
