import styled from 'styled-components';

const PriceTag = styled.span`
  background: rgb(var(--cream));
  color: rgb(var(--turquoise));
  padding: 5px 10px;
  line-height: 1;
  font-size: 1.5rem;
  display: inline-block;
  position: absolute;
  top: 160px;
  right: -5px;

  &::after {
    content: ' ';
    display: block;
    width: 25px;
    height: 100%;
    background: rgb(var(--cream));
    position: absolute;
    top: -10px;
    right: -20px;
    border-right: 10px solid white;
  }
`;

export default PriceTag;
