import Link from 'next/link';
import styled from 'styled-components';
import React, { useState, useEffect } from 'react';

const NavbarStyles = styled.nav`
  display: flex;
  flex-wrap: nowrap;
  justify-content: space-between;
  align-items: center;
  margin: 0;
  padding: 5px 10px;
  color: rgb(var(--cream));
  font-family: var(--titleFont);
  position: fixed;
  width: 100%;
  border-bottom: 1px solid rgba(var(--cream), 0.2);

  a {
    color: rgb(var(--cream), 0.7);
    text-decoration: none;
    padding: 15px 5px;
    margin: 0 5px;
    display: inline-block;

    transition: all 0.3s;
  }

  a:hover {
    color: rgb(var(--cream));
  }

  h2 {
    text-transform: uppercase;
    padding: 0;
    margin: 0 15px;
    display: inline-block;
  }

  .sub-bar {
    display: inline-block;
    min-width: 40%;
    flex-grow: 1;
    border: 1px solid rgb(var(--cream), 0.3);
    border-radius: 5px;
    color: black;
    margin: 0 10px;
    text-align: left;
    background: rgb(var(--cream), 0.5);
  }

  .sub-bar p {
    padding: 5px 10px;
    margin: 0;
    font-family: var(--textFont);
    color: white;
  }

  .sections {
    flex-basis: 80%;
    text-align: right;
  }
`;

const Nav = () => {
  const [scroll, setScroll] = useState(false);

  useEffect(() => {
    document.addEventListener('scroll', function () {
      if (document.scrollingElement.scrollTop >= 50) {
        setScroll(true);
      } else {
        setScroll(false);
      }
    });
  }, [scroll]);

  return (
    <NavbarStyles
      style={{
        backgroundColor: scroll ? 'rgba(255, 255, 255, 0.9)' : 'transparent',
      }}
    >
      <Link href="/">
        <h2>UTime</h2>
      </Link>
      <div className="sections">
        <div className="sub-bar">
          <p>Search...</p>
        </div>
        <Link href="/products">Products</Link>
        <Link href="/sell">Sell</Link>
        <Link href="/orders">Orders</Link>
        <Link href="/account">Account</Link>
      </div>
    </NavbarStyles>
  );
};

export default Nav;
